USE ghibli;

CREATE TABLE moviesghibli (
  id INT NOT NULL AUTO_INCREMENT,
  nombre VARCHAR(255) NOT NULL,
  anio INT NOT NULL,
  director VARCHAR(255) NOT NULL,
  descripcion TEXT NOT NULL,
  PRIMARY KEY (id)
);

INSERT INTO `ghibli`.`moviesghibli` (`nombre`, `anio`, `director`, `descripcion`) VALUES ('El viaje de Chihiro', '2001', 'Hayao Miyazaki', 'La historia sigue a Chihiro, una niña de diez años que se muda a una nueva ciudad con sus padres. Durante un viaje, sus padres se transforman en cerdos y Chihiro se ve atrapada en un mundo mágico y sobrenatural. Para salvar a sus padres y regresar a su mundo, Chihiro debe trabajar en una casa de baños termales para dioses y espíritus. La película es una aventura emocionante y conmovedora que explora temas como la madurez, la amistad y la familia.'), ('La tumba de las luciérnagas', '1988', 'Isao Takahata', 'La historia se desarrolla en la Segunda Guerra Mundial y sigue a dos hermanos, Seita y Setsuko, que luchan por sobrevivir después de perder a su madre y su hogar en un bombardeo. La película es una conmovedora reflexión sobre la guerra, la pérdida y la supervivencia.'), ('La princesa Mononoke', '1997', 'Hayao Miyazaki', 'La historia se desarrolla en un Japón del período Muromachi, y se centra en la lucha entre los guardianes sobrenaturales de un bosque y los humanos que profanan sus recursos, vista por el forastero Ashitaka. La película es una reflexión conmovedora sobre la naturaleza, la tecnología y la relación entre los seres humanos y el medio ambiente.'),('Mi vecino Totoro', '1988', 'Hayao Miyazaki', 'La historia sigue a dos hermanas, Satsuki y Mei, que se mudan al campo para estar cerca del hospital donde su madre se está recuperando de una enfermedad. Allí, descubren un mundo mágico habitado por espíritus del bosque, incluyendo a Totoro, un espíritu amistoso y protector. La película es una conmovedora reflexión sobre la familia, la amistad y la imaginación.'), ('El castillo en el cielo', '1986', 'Hayao Miyazaki', 'La historia sigue a Pazu y Sheeta, dos jóvenes que intentan evitar que una antigua piedra mágica caiga en manos de un grupo de agentes militares, quienes intentarán usarla para llegar a una legendaria isla flotante llamada “Laputa”. La película es una emocionante aventura llena de acción y misterio que explora temas como la amistad, la lealtad y la importancia de proteger nuestro patrimonio cultural y natural. '), ('El castillo ambulante', '2004', 'Hayao Miyazaki', 'La historia sigue a Sophie, una joven sombrerera que es hechizada por una bruja y se convierte en una anciana. Sophie busca la ayuda del mago Howl, quien vive en un castillo ambulante, para romper el hechizo. La película es una emocionante aventura llena de acción y misterio que explora temas como la identidad, la amistad y la importancia de proteger nuestro patrimonio cultural y natural.. '), ('Recuerdos del ayer', '1991', 'Isao Takahata', 'La historia sigue a Taeko Okajima, una joven asalariada de veintisiete años que pide permiso de vacaciones para poder regresar a su casa en el pueblo rural de su cuñada, y donde guarda tiernos momentos. En el transcurso de su viaje a la granja de sus familiares y a su posterior estancia, irá recordando su infancia, sus días en la escuela y otras vivencias peculiares, mientras vive intensamente su presente. La película es una emotiva reflexión sobre la vida, la nostalgia y la importancia de conectarse con nuestras raíces.'), ('Nicky, la aprendiz de bruja', '1989', 'Hayao Miyazaki', 'La historia sigue a Kiki, una joven bruja de 13 años que, según la tradición, debe abandonar su hogar durante un año para desarrollar sus poderes y aprender a valerse por sí misma. Kiki se instala en una ciudad marítima donde comienza a conocer el mundo y a conocerse a sí misma. Durante su viaje, Kiki conoce a grandes amigos, como Tombo y Osono.'), ('El recuerdo de Marnie', '2014', 'Hiromasa Yonebayashi', 'La historia sigue a Anna, una joven solitaria y sin amigos que es enviada a pasar el verano con sus tíos en una pequeña ciudad costera. Allí, Anna descubre una casa abandonada en la que conoce a Marnie, una chica misteriosa y fascinante con la que establece una amistad profunda. A medida que Anna profundiza en la vida de Marnie, descubre secretos sorprendentes sobre su propia familia y su pasado. '), ('La colina de las amapolas', '2011', 'Goro Miyazaki', 'Umi Matsuzaki, una estudiante de secundaria, descubre una casa abandonada en la que conoce a Shun Kazama, un muchacho miembro del club de periodismo, y Shirō Mizunuma, presidente del consejo estudiantil. Ambos son representantes del Quartier Latin, un edificio antiguo que alberga las diferentes asociaciones y grupos de estudiantes y que corre peligro de ser demolido por la renovación de edificios con motivo de los Juegos Olímpicos de Tokio de 1964.');


CREATE TABLE postersghibli (
  idposter INT NOT NULL AUTO_INCREMENT,
  poster VARCHAR(255) NOT NULL,
  fk_movieposters INT NOT NULL,
  PRIMARY KEY (idposter),
  FOREIGN KEY (fk_movieposters) REFERENCES moviesghibli(id)
);

